import math

def dist(x1, y1, x2, y2):
    return math.sqrt((x1 - x2) ** 2 + (y1 - y2) ** 2)

def closest_point(x, y, points):
    min_dist = float('inf')
    for px, py in points:
        dist_val = dist(x, y, px, py)
        if dist_val < min_dist:
            min_dist = dist_val
    return min_dist

def check_spotlight(sx, sy, ex, ey, r, points):
    min_dist = float('inf')
    for i in range(len(points)):
        x1, y1 = points[i]
        x2, y2 = points[(i + 1) % len(points)]
        dist_val = dist(sx, sy, x1, y1)
        if dist_val < min_dist:
            min_dist = dist_val
        dist_val = dist(ex, ey, x2, y2)
        if dist_val < min_dist:
            min_dist = dist_val
    return min_dist > r + 0.000001

def check_all_spotlights(sx, sy, ex, ey, spotlights):
    for r, points in spotlights:
        if not check_spotlight(sx, sy, ex, ey, r, points):
            return False
    return True

def main():
    N = int(input())
    sx, sy = map(int, input().split())
    ex, ey = map(int, input().split())
    spotlights = []
    for _ in range(N):
        r = int(input())
        K = int(input().split()[0])
        points = []
        for _ in range(K):
            x, y = map(int, input().split())
            points.append((x, y))
        spotlights.append((r, points))
    if check_all_spotlights(sx, sy, ex, ey, spotlights):
        print("Yes")
    else:
        print("No")

if __name__ == "__main__":
    main()
