import java.io.*;
import java.util.*;

public class Main {
    static final int MAXN = 55;
    static final int MAXM = 105;
    static final double EPS = 1e-8;

    static class Point {
        double x, y;

        Point(double x, double y) {
            this.x = x;
            this.y = y;
        }
    }

    static class Rect {
        Point p1, p2;

        Rect(Point p1, Point p2) {
            this.p1 = p1;
            this.p2 = p2;
        }
    }

    static double prob(double a, double b) {
        return (b - a) / (MAXM - 1);
    }

    static double calc(Rect r, double mA1, double mA2, double mB1, double mB2, double mX) {
        double sum = 0;
        for (int i = r.p1.x; i <= r.p2.x; i++) {
            for (int j = r.p1.y; j <= r.p2.y; j++) {
                if (grid[i][j] == 'A') {
                    sum += (mA2 - mA1) * (j - r.p1.y) + mA1;
                } else if (grid[i][j] == 'B') {
                    sum += (mB2 - mB1) * (j - r.p1.y) + mB1;
                } else if (grid[i][j] == 'X') {
                    sum += mX * (j - r.p1.y);
                }
            }
        }
        return sum;
    }

    static char[][] grid = new char[MAXN][MAXN];

    public static void main(String[] args) throws IOException {
        BufferedReader in = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter out = new PrintWriter(System.out);

        StringTokenizer st = new StringTokenizer(in.readLine());
        int H = Integer.parseInt(st.nextToken());
        int W = Integer.parseInt(st.nextToken());

        st = new StringTokenizer(in.readLine());
        double mA1 = Double.parseDouble(st.nextToken());
        double mA2 = Double.parseDouble(st.nextToken());
        double mB1 = Double.parseDouble(st.nextToken());
        double mB2 = Double.parseDouble(st.nextToken());
        double mX = Double.parseDouble(st.nextToken());

        for (int i = 1; i <= H; i++) {
            String s = in.readLine();
            for (int j = 1; j <= W; j++) {
                grid[i][j] = s.charAt(j - 1);
            }
        }

        double ans = 0;
        for (int i = 1; i <= H; i++) {
            for (int j = 1; j <= W; j++) {
                if (grid[i][j] == '.') continue;
                Rect r = new Rect(new Point(i, j), new Point(i + 1, j + 1));
                double sum = calc(r, mA1, mA2, mB1, mB2, mX);
                double probA = prob(mA1, mA2);
                double probB = prob(mB1, mB2);
                double prob = (probA * probB) * (sum / (probA * probB * sum + mX * (r.p2.x - r.p1.x) * (r.p2.y - r.p1.y)));
                ans += prob;
            }
        }

        out.printf("%.10f\n", 1 - ans + EPS);
        out.close();
    }
}
