#include <stdio.h>
#include <limits.h>

#define MAX_BRANCHING_POINTS 1000
#define MAX_CHOICES 50
#define MAX_TIME 300
#define MAX_ID 1000

int min(int a, int b) {
    return (a < b) ? a : b;
}

int main() {
    int n, k, i, j, t, c, id, time, min_time = INT_MAX;
    int graph[MAX_BRANCHING_POINTS][MAX_CHOICES + 1];
    int visited[MAX_BRANCHING_POINTS];
    int distance[MAX_BRANCHING_POINTS];

    scanf("%d", &n);
    for (i = 0; i < n; i++) {
        scanf("%d", &k);
        for (j = 0; j <= k; j++) {
            scanf("%d %d", &graph[i][j], &c);
        }
    }

    for (i = 0; i < n; i++) {
        for (j = 0; j < n; j++) {
            distance[j] = INT_MAX;
        }
        distance[i] = 0;
        for (j = 0; j < n; j++) {
            visited[j] = 0;
        }
        for (j = 0; j < n; j++) {
            id = -1;
            for (t = 0; t < n; t++) {
                if (!visited[t] && (id == -1 || distance[t] < distance[id])) {
                    id = t;
                }
            }
            if (id == -1) {
                break;
            }
            visited[id] = 1;
            for (t = 0; t <= k; t++) {
                if (graph[id][t] != 0) {
                    time = distance[id] + c;
                    if (graph[id][t] == 0) {
                        time += graph[id][0];
                    } else {
                        time += graph[id][t];
                    }
                    distance[graph[id][t] - 1] = min(distance[graph[id][t] - 1], time);
                }
            }
        }
        min_time = min(min_time, distance[n - 1]);
    }
    printf("%d\n", min_time);
    return 0;
}
