#include <iostream>
#include <vector>
#include <queue>
#include <limits>

using namespace std;

const int INF = numeric_limits<int>::max();

int main() {
    int n;
    cin >> n;

    vector<vector<pair<int, int>>> graph(n);
    for (int i = 0; i < n; ++i) {
        int k;
        cin >> k;
        for (int j = 0; j < k; ++j) {
            int t, c;
            cin >> t >> c;
            --t;
            graph[i].emplace_back(t, c);
        }
    }

    vector<int> dist(n, INF);
    dist[0] = 0;

    priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> pq;
    pq.emplace(0, 0);

    while (!pq.empty()) {
        int d, u;
        tie(d, u) = pq.top();
        pq.pop();

        if (d != dist[u]) continue;

        for (const auto& [v, c] : graph[u]) {
            int nd = d + c;
            if (nd < dist[v]) {
                dist[v] = nd;
                pq.emplace(nd, v);
            }
        }
    }

    int ans = 0;
    for (int i = 0; i < n; ++i) {
        ans = max(ans, dist[i]);
    }

    cout << ans << endl;

    return 0;
}
