#include <iostream>
#include <vector>
#include <algorithm>

int minFrustration(int n, std::vector<int> a, std::vector<int> w) {
    int frustration = 0;
    for (int i = 0; i < n; i++) {
        if (a[i] == 0) {
            if (i > 0 && a[i - 1] == 1) {
                frustration += w[i];
            }
            if (i + 1 < n && a[i + 1] == 1) {
                frustration += w[i];
            }
        } else {
            if (i > 0 && a[i - 1] == 0) {
                frustration += w[i];
            }
            if (i + 1 < n && a[i + 1] == 0) {
                frustration += w[i];
            }
        }
    }
    return frustration;
}

int main() {
    int n;
    std::cin >> n;
    std::vector<int> a(n), w(n);
    for (int i = 0; i < n; i++) {
        std::cin >> a[i];
    }
    for (int i = 0; i < n; i++) {
        std::cin >> w[i];
    }
    std::vector<int> frustration(n);
    for (int i = 0; i < n; i++) {
        frustration[i] = minFrustration(n, a, w);
        a.push_back(a[0]);
        w.push_back(w[0]);
        a.erase(a.begin());
        w.erase(w.begin());
    }
    std::cout << *std::min_element(frustration.begin(), frustration.end()) << std::endl;
    return 0;
}
