import sys
import heapq

def dijkstra(n, edges):
    graph = [[] for _ in range(n + 1)]
    for u, v, w in edges:
        graph[u].append((v, w))

    dist = [float('inf')] * (n + 1)
    dist[1] = 0
    pq = [(0, 1)]

    while pq:
        d, u = heapq.heappop(pq)
        if d > dist[u]:
            continue
        for v, w in graph[u]:
            if dist[v] > dist[u] + w:
                dist[v] = dist[u] + w
                heapq.heappush(pq, (dist[v], v))

    return dist

def main():
    n = int(sys.stdin.readline())
    edges = []
    for i in range(n):
        k = list(map(int, sys.stdin.readline().split()))[1:]
        for j in range(0, len(k), 2):
            u, v, w = i + 1, k[j], k[j + 1]
            edges.append((u, v, w))

    dist = dijkstra(n, edges)
    ans = float('inf')
    for i in range(1, n + 1):
        if dist[i] != float('inf'):
            ans = min(ans, dist[i])

    print(ans)

if __name__ == "__main__":
    main()
