#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_W 2000
#define MAX_H 2

int w, h;
char s[MAX_H][MAX_W + 1], t[MAX_H][MAX_W + 1];
int dp[MAX_H][MAX_W + 1][MAX_H][MAX_W + 1];

int dfs(int x, int y, int sx, int sy) {
    if (dp[x][y][sx][sy] != -1) return dp[x][y][sx][sy];
    if (x == h && y == w) return 0;
    int res = 1e9;
    if (x < h - 1 && y < w - 1) {
        int cnt = 0;
        for (int i = x; i <= x + 1; i++) {
            for (int j = y; j <= y + 1; j++) {
                if (s[i][j] == 'o') cnt++;
            }
        }
        for (int i = x; i <= x + 1; i++) {
            for (int j = y; j <= y + 1; j++) {
                if (s[i][j] == 'o') {
                    s[i][j] = '.';
                    res = std::min(res, 1 + dfs(x + 1, y, sx, sy));
                    s[i][j] = 'o';
                }
            }
        }
        for (int i = x; i <= x + 1; i++) {
            for (int j = y; j <= y + 1; j++) {
                if (s[i][j] == 'o') {
                    t[i][j] = '.';
                    res = std::min(res, 1 + dfs(x + 1, y, sx, sy));
                    t[i][j] = 'o';
                }
            }
        }
    }
    if (x < h - 1) {
        for (int i = y; i <= w; i++) {
            if (s[x][i] == 'o') {
                s[x][i] = '.';
                res = std::min(res, 1 + dfs(x + 1, y, sx, sy));
                s[x][i] = 'o';
            }
        }
    }
    if (y < w - 1) {
        for (int i = x; i <= h; i++) {
            if (s[i][y] == 'o') {
                s[i][y] = '.';
                res = std::min(res, 1 + dfs(x, y + 1, sx, sy));
                s[i][y] = 'o';
            }
        }
    }
    dp[x][y][sx][sy] = res;
    return res;
}

int main() {
    scanf("%d", &w);
    for (int i = 0; i < h; i++) {
        scanf("%s", s[i]);
    }
    for (int i = 0; i < h; i++) {
        scanf("%s", t[i]);
    }
    memset(dp, -1, sizeof(dp));
    int res = dfs(0, 0, 0, 0);
    printf("%d\n", res);
    return 0;
}
