#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

const int MAX_W = 2000;

int w;
vector<string> s, t;
vector<vector<int>> dp;

int dfs(int x, int y) {
    if (x == w) return 0;
    if (dp[x][y] != -1) return dp[x][y];
    int res = 1e9;
    if (s[x][y] == 'o') {
        int cnt = 0;
        for (int i = x; i < w; i++) {
            if (s[i][y] == 'o') cnt++;
            else break;
        }
        for (int i = y; i < w; i++) {
            if (s[x][i] == 'o') cnt++;
            else break;
        }
        if (cnt == 2) {
            for (int i = x + 1; i < w; i++) {
                for (int j = y + 1; j < w; j++) {
                    if (s[i][j] == 'o') {
                        res = min(res, dfs(i, j) + 1);
                    }
                }
            }
        }
    }
    if (t[x][y] == 'o') {
        int cnt = 0;
        for (int i = x; i < w; i++) {
            if (t[i][y] == 'o') cnt++;
            else break;
        }
        for (int i = y; i < w; i++) {
            if (t[x][i] == 'o') cnt++;
            else break;
        }
        if (cnt == 2) {
            for (int i = x + 1; i < w; i++) {
                for (int j = y + 1; j < w; j++) {
                    if (t[i][j] == 'o') {
                        res = min(res, dfs(i, j) + 1);
                    }
                }
            }
        }
    }
    dp[x][y] = res;
    return res;
}

int main() {
    cin >> w;
    s.resize(w);
    t.resize(w);
    for (int i = 0; i < w; i++) {
        cin >> s[i];
    }
    for (int i = 0; i < w; i++) {
        cin >> t[i];
    }
    dp.resize(w, vector<int>(w, -1));
    cout << dfs(0, 0) << endl;
    return 0;
}
