import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.PrintWriter;
import java.util.*;

public class Main {
    static int W;
    static char[][] s, t;
    static int[][] cnt;
    static int ans;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());
        W = Integer.parseInt(st.nextToken());
        s = new char[2][];
        t = new char[2][];
        for (int i = 0; i < 2; i++) {
            s[i] = br.readLine().toCharArray();
            t[i] = br.readLine().toCharArray();
        }
        cnt = new int[2][W];
        for (int i = 0; i < 2; i++) {
            for (int j = 0; j < W; j++) {
                if (s[i][j] == 'o') cnt[i][j]++;
            }
        }
        for (int i = 0; i < 2; i++) {
            for (int j = 0; j < W; j++) {
                if (t[i][j] == 'o') cnt[i][j]--;
            }
        }
        for (int i = 0; i < 2; i++) {
            for (int j = 0; j < W; j++) {
                if (t[i][j] == 'o') {
                    if (cnt[i][j] == 0) {
                        ans++;
                        cnt[i][j] = 2;
                    } else if (cnt[i][j] == 1) {
                        ans++;
                        cnt[i][j] = 3;
                    }
                } else if (t[i][j] == '*') {
                    if (cnt[i][j] == 1) {
                        ans++;
                        cnt[i][j] = 3;
                    } else if (cnt[i][j] == 2) {
                        ans++;
                        cnt[i][j] = 1;
                    }
                }
            }
        }
        for (int i = 0; i < 2; i++) {
            for (int j = 0; j < W; j++) {
                if (cnt[i][j] == 1) {
                    int x = i, y = j;
                    while (x < 2 && y < W && cnt[x][y] == 1) {
                        cnt[x][y] = 2;
                        x++;
                        y++;
                    }
                    x = i; y = j;
                    while (x >= 0 && y >= 0 && cnt[x][y] == 1) {
                        cnt[x][y] = 2;
                        x--;
                        y--;
                    }
                    x = i; y = j;
                    while (x < 2 && y >= 0 && cnt[x][y] == 1) {
                        cnt[x][y] = 2;
                        x++;
                        y--;
                    }
                    x = i; y = j;
                    while (x >= 0 && y < W && cnt[x][y] == 1) {
                        cnt[x][y] = 2;
                        x--;
                        y++;
                    }
                }
            }
        }
        for (int i = 0; i < 2; i++) {
            for (int j = 0; j < W; j++) {
                if (cnt[i][j] == 2) {
                    ans++;
                }
            }
        }
        pw.println(ans);
        pw.close();
    }
}
