import sys
input = sys.stdin.readline

def get(x, y):
    return (x - 1) * W + y

def check():
    for i in range(1, H + 1):
        for j in range(1, W + 1):
            if (i + j) % 2 == 0:
                if S[i][j] == 'o' and T[i][j] == '.':
                    return False
                if S[i][j] == '.' and T[i][j] == 'o':
                    return False
    return True

def solve():
    global S, T, H, W
    H, W = map(int, input().split())
    S = [list(input().rstrip()) for _ in range(2)]
    T = [list(input().rstrip()) for _ in range(2)]
    for i in range(1, H + 1):
        for j in range(1, W + 1):
            if (i + j) % 2 == 0:
                if S[i][j] == 'o':
                    S[i][j] = 1
                else:
                    S[i][j] = 0
                if T[i][j] == 'o':
                    T[i][j] = 1
                elif T[i][j] == '.':
                    T[i][j] = 0
                else:
                    T[i][j] = -1
    dp = [[float('inf')] * (1 << (W * 2)) for _ in range(H * W + 1)]
    dp[0][0] = 0
    for i in range(H * W):
        for mask in range(1 << (W * 2)):
            if dp[i][mask] == float('inf'):
                continue
            for j in range(1, W + 1):
                if (i + j) % 2 == 0:
                    continue
                x, y = i + 1, j
                if (x + y) % 2 == 0:
                    continue
                new_mask = mask
                if S[x][y] == 1 and T[x][y] == 0:
                    new_mask |= 1 << get(x, y)
                elif S[x][y] == 0 and T[x][y] == 1:
                    new_mask |= 1 << get(x, y)
                elif S[x][y] == 1 and T[x][y] == 1:
                    new_mask &= ~(1 << get(x, y))
                elif S[x][y] == 0 and T[x][y] == 0:
                    new_mask &= ~(1 << get(x, y))
                dp[i + 1][new_mask] = min(dp[i + 1][new_mask], dp[i][mask] + 1)
    ans = float('inf')
    for mask in range(1 << (W * 2)):
        if check():
            ans = min(ans, dp[H * W][mask])
    print(ans)

solve()
