#include <stdio.h>
#include <stdlib.h>

#define MAXN 100005

int n, ans;
int depth[MAXN], parent[MAXN], child[MAXN][2];

void dfs(int u, int d) {
    depth[u] = d;
    ans += d - depth[parent[u]];
    if (child[parent[u]][0] == u) {
        child[parent[u]][0] = child[u][0];
        child[parent[u]][1] = child[u][1];
    } else {
        child[parent[u]][1] = child[u][0];
        child[parent[u]][0] = child[u][1];
    }
    for (int i = 0; i < 2; i++) {
        if (child[u][i] != -1) {
            dfs(child[u][i], d + 1);
        }
    }
}

int main() {
    scanf("%d", &n);
    for (int i = 2; i <= n; i++) {
        scanf("%d", &parent[i]);
        child[parent[i]][0] = i;
        child[parent[i]][1] = -1;
    }
    dfs(1, 0);
    printf("%d\n", ans);
    return 0;
}
