import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        int n = scanner.nextInt();
        int[] a = new int[n];
        int[] w = new int[n];

        for (int i = 0; i < n; i++) {
            a[i] = scanner.nextInt();
        }

        for (int i = 0; i < n; i++) {
            w[i] = scanner.nextInt();
        }

        int frustration = 0;
        for (int i = 0; i < n; i++) {
            if (a[i] == 0) {
                for (int j = i + 1; j < n; j++) {
                    if (a[j] == 1) {
                        frustration += w[i];
                        break;
                    }
                }
            } else {
                for (int j = i + 1; j < n; j++) {
                    if (a[j] == 0) {
                        frustration += w[i];
                        break;
                    }
                }
            }
        }

        System.out.println(frustration);
    }
}
