#define MAXLEN 51

int is_valid(char *s) {
    int i, j, count = 0;
    for (i = 0; i < strlen(s); i++) {
        if (s[i] == '(') count++;
        else if (s[i] == ')') {
            if (count == 0) return 0;
            count--;
        }
    }
    return count == 0;
}

int is_number(char *s) {
    int i;
    for (i = 0; i < strlen(s); i++) {
        if (!isdigit(s[i])) return 0;
    }
    return 1;
}

