#include <iostream>
#include <string>
#include <vector>
#include <algorithm>
#include <cctype>

using namespace std;

bool isDigit(char c) {
    return c >= '0' && c <= '9';
}

bool isUsable(char c) {
    return c == 'L' || c == 'R' || c == '(' || c == ')' || c == ',' || isDigit(c);
}

int evaluate(const string& s) {
    int num = 0;
    for (char c : s) {
        if (isDigit(c)) {
            num = num * 10 + (c - '0');
        }
    }
    return num;
}

string replaceQuestionMarks(const string& s) {
    string result;
    for (char c : s) {
        if (c == '?') {
            result += '0';
        } else {
            result += c;
        }
    }
    return result;
}

bool isValid(const string& s) {
    int count = 0;
    for (char c : s) {
        if (c == '(') {
            count++;
        } else if (c == ')') {
            count--;
            if (count < 0) {
                return false;
            }
        }
    }
    return count == 0;
}

int main() {
    string s;
    cin >> s;
    s = replaceQuestionMarks(s);
    if (!isValid(s)) {
        cout << "invalid" << endl;
        return 0;
    }
    int max = 0;
    for (int i = 0; i < s.size(); i++) {
        if (s[i] == 'L') {
            for (int j = i + 1; j < s.size(); j++) {
                if (s[j] == ',') {
                    int num1 = evaluate(s.substr(i + 1, j - i - 1));
                    int num2 = evaluate(s.substr(j + 1));
                    max = max > num2 ? max : num2;
                }
            }
        } else if (s[i] == 'R') {
            for (int j = i + 1; j < s.size(); j++) {
                if (s[j] == ',') {
                    int num1 = evaluate(s.substr(i + 1, j - i - 1));
                    int num2 = evaluate(s.substr(j + 1));
                    max = max > num1 ? max : num1;
                }
            }
        }
    }
    cout << max << endl;
    return 0;
}
