#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

int main() {
    int n;
    cin >> n;

    vector<int> open(n, 0);
    vector<int> close(n, 0);

    for (int i = 0; i < n; i++) {
        string s;
        cin >> s;

        for (char c : s) {
            if (c == '(') open[i]++;
            else if (c == ')') close[i]++;
        }
    }

    int openTotal = 0;
    int closeTotal = 0;

    for (int i = 0; i < n; i++) {
        openTotal += open[i];
        closeTotal += close[i];
    }

    if (openTotal != closeTotal || openTotal % 2 != 0) {
        cout << "No" << endl;
        return 0;
    }

    int openLeft = openTotal / 2;
    int closeLeft = closeTotal / 2;

    int openRight = 0;
    int closeRight = 0;

    for (int i = 0; i < n; i++) {
        if (open[i] == close[i]) {
            if (open[i] > 0) {
                openLeft -= open[i] / 2;
                closeRight += open[i] / 2;
            } else {
                closeLeft -= close[i] / 2;
                openRight += close[i] / 2;
            }
        } else if (open[i] > close[i]) {
            openLeft -= open[i] / 2;
            closeRight += open[i] / 2;
        } else {
            closeLeft -= close[i] / 2;
            openRight += close[i] / 2;
        }

        if (openLeft < 0 || closeLeft < 0 || openRight < 0 || closeRight < 0) {
            cout << "No" << endl;
            return 0;
        }
    }

    cout << "Yes" << endl;

    return 0;
}
