#include <stdio.h>
#include <stdlib.h>

typedef struct {
    int x, y;
} Point;

int compare(const void *a, const void *b) {
    Point *p1 = (Point *)a;
    Point *p2 = (Point *)b;
    return (p1->x - p2->x) * (p1->x - p2->x) + (p1->y - p2->y) * (p1->y - p2->y);
}

int main() {
    int n;
    scanf("%d", &n);
    Point *points = (Point *)malloc((n + 1) * sizeof(Point));
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &points[i].x, &points[i].y);
    }
    points[n].x = points[0].x;
    points[n].y = points[0].y;
    qsort(points, n + 1, sizeof(Point), compare);
    int min = 0, max = n;
    while (min < max) {
        int mid = (min + max) / 2;
        int left = 0, right = n;
        while (left < right) {
            int mid2 = (left + right) / 2;
            if (points[mid2].y > points[mid].y) {
                right = mid2;
            } else {
                left = mid2 + 1;
            }
        }
        int count = 0;
        for (int i = left; i < n; i++) {
            if (points[i].y > points[mid].y) {
                count++;
            }
        }
        if (count == 0) {
            max = mid - 1;
        } else {
            min = mid;
        }
    }
    printf("%d\n", min + 1);
    free(points);
    return 0;
}
