import java.util.Scanner;

public class Main {
    static int N;
    static int[][] P = new int[100][2];

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        N = scanner.nextInt();
        for (int i = 0; i < N; i++) {
            P[i][0] = scanner.nextInt();
            P[i][1] = scanner.nextInt();
        }
        P[N][0] = P[0][0];
        P[N][1] = P[0][1];
        int ans = 1;
        for (int i = 0; i < N; i++) {
            int x = P[i][0];
            int y = P[i][1];
            int x1 = P[(i + 1) % N][0];
            int y1 = P[(i + 1) % N][1];
            if (x == x1) {
                if (y < y1) {
                    while (i >= 0 && P[i][0] == x && P[i][1] < y1) {
                        i--;
                    }
                    if (i < 0) {
                        ans++;
                    }
                } else {
                    while (i >= 0 && P[i][0] == x && P[i][1] > y1) {
                        i--;
                    }
                    if (i < 0) {
                        ans++;
                    }
                }
            } else {
                if (x < x1) {
                    while (i >= 0 && P[i][0] < x1 && P[i][1] == y) {
                        i--;
                    }
                    if (i < 0) {
                        ans++;
                    }
                } else {
                    while (i >= 0 && P[i][0] > x1 && P[i][1] == y) {
                        i--;
                    }
                    if (i < 0) {
                        ans++;
                    }
                }
            }
        }
        System.out.println(ans);
    }
}
