#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MAX_PARTIES 30000

int main() {
    int N, M;
    scanf("%d %d", &N, &M);

    int S[MAX_PARTIES];
    for (int i = 0; i < M; i++) {
        scanf("%d", &S[i]);
    }

    int min_votes[MAX_PARTIES];
    int max_votes[MAX_PARTIES];

    int total_seats = 0;
    for (int i = 0; i < M; i++) {
        total_seats += S[i];
    }

    if (total_seats > N) {
        printf("impossible\n");
        return 0;
    }

    for (int i = 0; i < M; i++) {
        if (S[i] == 0) {
            min_votes[i] = 0;
            max_votes[i] = 0;
        } else {
            int min = (N / S[i]) + 1;
            int max = N / (S[i] - 1);
            if (min > N) {
                min = N;
            }
            if (max < 1) {
                max = 1;
            }
            min_votes[i] = min;
            max_votes[i] = max;
        }
    }

    for (int i = 0; i < M; i++) {
        if (min_votes[i] > max_votes[i]) {
            printf("impossible\n");
            return 0;
        }
    }

    for (int i = 0; i < M; i++) {
        printf("%d %d\n", min_votes[i], max_votes[i]);
    }

    return 0;
}
