#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

int main() {
    int N, M;
    cin >> N >> M;

    vector<int> S(M);
    for (int i = 0; i < M; i++) {
        cin >> S[i];
    }

    vector<int> S2(M);
    for (int i = 0; i < M; i++) {
        S2[i] = S[i] + 1;
    }

    sort(S2.begin(), S2.end());

    int sum = 0;
    for (int i = 0; i < M; i++) {
        sum += S2[i];
    }

    if (sum > N) {
        cout << "impossible" << endl;
        return 0;
    }

    vector<int> min(M), max(M);
    for (int i = 0; i < M; i++) {
        min[i] = S[i] * (S2[i] + 1) / (sum - S2[i]);
        max[i] = S[i] * (S2[M - 1] + 1) / (sum - S2[M - 1]);
    }

    for (int i = 0; i < M; i++) {
        if (min[i] > N) {
            cout << "impossible" << endl;
            return 0;
        }
        if (max[i] > N) {
            max[i] = N;
        }
        cout << min[i] << " " << max[i] << endl;
    }

    return 0;
}
