import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.PrintWriter;
import java.util.*;

public class Main {
    static class Pair implements Comparable<Pair> {
        long first;
        long second;

        public Pair(long first, long second) {
            this.first = first;
            this.second = second;
        }

        @Override
        public int compareTo(Pair o) {
            return Long.compare(this.first, o.first);
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());
        long n = Long.parseLong(st.nextToken());
        int m = Integer.parseInt(st.nextToken());
        long[] s = new long[m];
        for (int i = 0; i < m; i++) {
            s[i] = Long.parseLong(br.readLine());
        }
        long[] min = new long[m];
        long[] max = new long[m];
        for (int i = 0; i < m; i++) {
            min[i] = 0;
            max[i] = 0;
        }
        long sum = 0;
        for (int i = 0; i < m; i++) {
            sum += s[i];
        }
        if (sum * (m + 1) < n) {
            pw.println("impossible");
            pw.close();
            return;
        }
        for (int i = 0; i < m; i++) {
            if (s[i] == 0) {
                min[i] = 0;
                max[i] = 0;
                continue;
            }
            long l = 0;
            long r = (n + s[i] - 1) / s[i];
            while (r - l > 1) {
                long mid = (l + r) / 2;
                if (mid * (m + 1) - sum >= s[i]) {
                    l = mid;
                } else {
                    r = mid;
                }
            }
            min[i] = l * s[i];
            max[i] = (n + s[i] - 1) / s[i] * s[i];
        }
        for (int i = 0; i < m; i++) {
            if (min[i] == 0 && max[i] == 0) {
                boolean flag = false;
                for (int j = 0; j < m; j++) {
                    if (min[j] != 0 && max[j] != 0) {
                        flag = true;
                        break;
                    }
                }
                if (!flag) {
                    pw.println("impossible");
                    pw.close();
                    return;
                }
            }
        }
        for (int i = 0; i < m; i++) {
            if (min[i] == 0 && max[i] == 0) {
                continue;
            }
            pw.println(min[i] + " " + max[i]);
        }
        pw.close();
    }
}
