#include <stdio.h>

int main() {
    int w, h, i, j, a[1000], b[1000];

    scanf("%d %d", &w, &h);
    for (i = 0; i < w; i++) scanf("%d", &a[i]);
    for (j = 0; j < h; j++) scanf("%d", &b[j]);

    int sum_a = 0, sum_b = 0;
    for (i = 0; i < w; i++) sum_a += a[i];
    for (j = 0; j < h; j++) sum_b += b[j];

    if (sum_a == w && sum_b == h) printf("1\n");
    else printf("0\n");

    return 0;
}
