#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXLEN 1000000

typedef struct {
    char c;
    int len;
} RLE;

RLE* split(char* str) {
    int n = 0;
    char* token = strtok(str, " ");
    while (token != NULL) {
        RLE* rle = (RLE*)malloc(sizeof(RLE));
        rle->c = token[0];
        token = strtok(NULL, " ");
        if (token != NULL) {
            rle->len = atoi(token);
        } else {
            rle->len = 0;
        }
        n++;
        if (n == 1) {
            rle->next = NULL;
        } else {
            RLE* prev = split(str);
            prev->next = rle;
        }
        str = token;
    }
    return (RLE*)malloc(sizeof(RLE));
}

void print(RLE* rle) {
    while (rle != NULL) {
        printf("%c %d ", rle->c, rle->len);
        rle = rle->next;
    }
    printf("\n");
}

void replace(RLE* a, RLE* b, RLE* c) {
    RLE* prev = NULL;
    RLE* curr = a;
    while (curr != NULL) {
        if (curr->c == b->c && curr->len == b->len) {
            if (prev != NULL) {
                prev->len += c->len;
                free(c);
                return;
            } else {
                a->len += c->len;
                free(c);
                return;
            }
        }
        prev = curr;
        curr = curr->next;
    }
    free(c);
}

int main() {
    char str1[MAXLEN], str2[MAXLEN], str3[MAXLEN];
    fgets(str1, MAXLEN, stdin);
    fgets(str2, MAXLEN, stdin);
    fgets(str3, MAXLEN, stdin);
    str1[strcspn(str1, "\n")] = 0;
    str2[strcspn(str2, "\n")] = 0;
    str3[strcspn(str3, "\n")] = 0;
    RLE* a = split(str1);
    RLE* b = split(str2);
    RLE* c = split(str3);
    replace(a, b, c);
    print(a);
    return 0;
}
