#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

std::string replaceRLE(const std::string& A, const std::string& B, const std::string& C) {
    std::vector<std::pair<char, int>> Aseq, Bseq, Cseq;

    size_t pos = 0;
    while (pos < A.size()) {
        size_t spacePos = A.find(' ', pos);
        if (spacePos == std::string::npos) {
            break;
        }
        char c = A[pos];
        int len = std::stoi(A.substr(spacePos + 1, A.find(' ', spacePos + 1) - spacePos - 1));
        Aseq.push_back({c, len});
        pos = spacePos + 1;
    }

    pos = 0;
    while (pos < B.size()) {
        size_t spacePos = B.find(' ', pos);
        if (spacePos == std::string::npos) {
            break;
        }
        char c = B[pos];
        int len = std::stoi(B.substr(spacePos + 1, B.find(' ', spacePos + 1) - spacePos - 1));
        Bseq.push_back({c, len});
        pos = spacePos + 1;
    }

    pos = 0;
    while (pos < C.size()) {
        size_t spacePos = C.find(' ', pos);
        if (spacePos == std::string::npos) {
            break;
        }
        char c = C[pos];
        int len = std::stoi(C.substr(spacePos + 1, C.find(' ', spacePos + 1) - spacePos - 1));
        Cseq.push_back({c, len});
        pos = spacePos + 1;
    }

    std::string result = A;
    bool found = false;
    size_t i = 0;
    while (i < Aseq.size()) {
        if (Aseq[i].first == Bseq[0].first) {
            int j = 0;
            while (j < Bseq.size() && i + j < Aseq.size() && Aseq[i + j].first == Bseq[j].first && Aseq[i + j].second == Bseq[j].second) {
                j++;
            }
            if (j == Bseq.size()) {
                found = true;
                for (size_t k = 0; k < Cseq.size(); k++) {
                    result += std::to_string(Cseq[k].second) + ' ' + std::string(1, Cseq[k].first);
                }
                for (size_t k = i + j; k < Aseq.size(); k++) {
                    result += std::to_string(Aseq[k].second) + ' ' + std::string(1, Aseq[k].first);
                }
                break;
            }
        }
        i++;
    }

    if (!found) {
        return result;
    }

    size_t pos = result.find_last_of(' ');
    result.erase(pos, result.size() - pos);
    return result + '$';
}

int main() {
    std::string A, B, C;
    std::getline(std::cin, A);
    std::getline(std::cin, B);
    std::getline(std::cin, C);
    std::cout << replaceRLE(A, B, C) << std::endl;
    return 0;
}
