import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        String a = scanner.nextLine();
        String b = scanner.nextLine();
        String c = scanner.nextLine();

        String[] aParts = a.split(" ");
        String[] bParts = b.split(" ");
        String[] cParts = c.split(" ");

        StringBuilder aBuilder = new StringBuilder();
        StringBuilder bBuilder = new StringBuilder();
        StringBuilder cBuilder = new StringBuilder();

        int aIndex = 0;
        int bIndex = 0;
        int cIndex = 0;

        while (aIndex < aParts.length && bIndex < bParts.length && cIndex < cParts.length) {
            if (aParts[aIndex].equals(bParts[bIndex])) {
                int count = Integer.parseInt(aParts[aIndex + 1]);
                while (count > 0) {
                    aBuilder.append(aParts[aIndex]).append(" ");
                    count--;
                }
                bIndex++;
                aIndex += 2;
            } else if (aParts[aIndex].equals(cParts[cIndex])) {
                int count = Integer.parseInt(aParts[aIndex + 1]);
                while (count > 0) {
                    cBuilder.append(aParts[aIndex]).append(" ");
                    count--;
                }
                cIndex++;
                aIndex += 2;
            } else {
                aBuilder.append(aParts[aIndex]).append(" ");
                aIndex += 2;
            }
        }

        while (bIndex < bParts.length) {
            int count = Integer.parseInt(bParts[bIndex + 1]);
            while (count > 0) {
                bBuilder.append(bParts[bIndex]).append(" ");
                count--;
            }
            bIndex += 2;
        }

        while (cIndex < cParts.length) {
            int count = Integer.parseInt(cParts[cIndex + 1]);
            while (count > 0) {
                cBuilder.append(cParts[cIndex]).append(" ");
                count--;
            }
            cIndex += 2;
        }

        String result = aBuilder.toString().trim() + cBuilder.toString().trim() + bBuilder.toString().trim() + "$";
        System.out.println(result);
    }
}
