#include <stdio.h>
#include <string.h>

#define MAX_H 55
#define MAX_W 55
#define MAX_K 13
#define MAX_S 13
#define MAX_T 13
#define MAX_T_MOVE 10005
#define MAX_T_CHECK 10005

int H, W, K, s, t, T_move, T_check;
char map[MAX_H][MAX_W];
int room_cnt[MAX_S];
int room_cnt_sum[MAX_S];
int room_cnt_max[MAX_S];
int room_cnt_max_sum[MAX_S];
int room_cnt_max_sum2[MAX_S];
int dp[MAX_K][1 << MAX_S];

int dfs(int k, int mask) {
    if (k == K) {
        return 0;
    }
    if (dp[k][mask] != -1) {
        return dp[k][mask];
    }
    int ans = MAX_T_MOVE * (K - k);
    for (int i = 0; i < K; i++) {
        if ((mask >> i) & 1) {
            continue;
        }
        int new_mask = mask | (1 << i);
        int new_ans = dfs(k + 1, new_mask);
        for (int j = 0; j < K; j++) {
            if ((new_mask >> j) & 1) {
                new_ans = max(new_ans, new_ans + T_check * room_cnt_max[j]);
            }
        }
        ans = min(ans, new_ans);
    }
    return dp[k][mask] = ans;
}

int main() {
    scanf("%d %d %d", &H, &W, &K);
    scanf("%d %d %d %d", &s, &t, &T_move, &T_check);
    for (int i = 0; i < H; i++) {
        scanf("%s", map[i]);
    }
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            if (map[i][j] != '.') {
                int c = map[i][j] - 'A';
                room_cnt[c]++;
                room_cnt_sum[c] += (i == s - 1 && j == t - 1) ? 0 : T_move;
                room_cnt_max[c] = max(room_cnt_max[c], room_cnt[c]);
                room_cnt_max_sum[c] += room_cnt_max[c];
                room_cnt_max_sum2[c] = max(room_cnt_max_sum2[c], room_cnt_max_sum[c]);
            }
        }
    }
    memset(dp, -1, sizeof(dp));
    printf("%d\n", dfs(0, 0) + T_move * 2);
    return 0;
}
