#include <iostream>
#include <vector>
#include <string>
#include <algorithm>
#include <limits>

using namespace std;

const int INF = numeric_limits<int>::max();

int h, w, k, s, t, move, check;
vector<string> map;
vector<vector<int>> rooms;
vector<vector<int>> dist;
vector<int> unit;
vector<int> order;
vector<int> used;
vector<int> min_dist;

void bfs(int u) {
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            dist[i][j] = INF;
        }
    }
    dist[s - 1][t - 1] = 0;
    vector<pair<int, int>> q;
    q.push_back({s - 1, t - 1});
    while (!q.empty()) {
        int x = q.back().first;
        int y = q.back().second;
        q.pop_back();
        for (int i = 0; i < 4; i++) {
            int nx = x + dx[i];
            int ny = y + dy[i];
            if (nx >= 0 && nx < h && ny >= 0 && ny < w && map[nx][ny] != '.' && dist[nx][ny] == INF) {
                dist[nx][ny] = dist[x][y] + 1;
                q.push_back({nx, ny});
            }
        }
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> h >> w >> k;
    cin >> s >> t >> move >> check;
    map.resize(h);
    for (int i = 0; i < h; i++) {
        cin >> map[i];
    }
    rooms.resize(26);
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            if (map[i][j] != '.') {
                rooms[map[i][j] - 'A'].push_back({i, j});
            }
        }
    }
    for (int i = 0; i < 26; i++) {
        sort(rooms[i].begin(), rooms[i].end());
    }
    unit.resize(26);
    order.resize(26);
    used.resize(26);
    min_dist.resize(26, INF);
    for (int i = 0; i < 26; i++) {
        unit[i] = i;
    }
    do {
        for (int i = 0; i < 26; i++) {
            used[i] = 0;
        }
        int cnt = 0;
        for (int i = 0; i < 26; i++) {
            if (used[unit[i]] == 0) {
                order[cnt++] = unit[i];
                used[unit[i]] = 1;
                for (int j = 0; j < 26; j++) {
                    if (used[unit[j]] == 0 && i != j) {
                        int u = unit[i];
                        int v = unit[j];
                        int cnt1 = 0;
                        int cnt2 = 0;
                        for (int k = 0; k < rooms[u].size(); k++) {
                            if (rooms[v].size() > 0 && rooms[v][0] == rooms[u][k]) {
                                cnt1++;
                            }
                        }
                        for (int k = 0; k < rooms[v].size(); k++) {
                            if (rooms[u].size() > 0 && rooms[u][0] == rooms[v][k]) {
                                cnt2++;
                            }
                        }
                        if (cnt1 > cnt2) {
                            unit[j] = unit[i];
                        } else if (cnt1 < cnt2) {
                            unit[i] = unit[j];
                        }
                    }
                }
            }
        }
        dist.resize(h, vector<int>(w, INF));
        for (int i = 0; i < 26; i++) {
            bfs(i);
        }
        int sum = 0;
        for (int i = 0; i < 26; i++) {
            min_dist[i] = INF;
            for (int j = 0; j < rooms[i].size(); j++) {
                min_dist[i] = min(min_dist[i], dist[rooms[i][j].first][rooms[i][j].second]);
            }
            sum += min_dist[i];
        }
        int ans = INF;
        for (int i = 0; i < k; i++) {
            int tmp = sum + (k - i - 1) * move + min_dist[order[i]] * 2 + move;
            ans = min(ans, tmp);
        }
    } while (next_permutation(unit.begin(), unit.end()));
    cout << ans << '\n';
    return 0;
}

const int dx[4] = {0, 1, 0, -1};
const int dy[4] = {1, 0, -1, 0};
