import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.PrintWriter;
import java.util.*;

public class Main {
    static int H, W, K, s, t, Tmove, Tcheck;
    static char[][] map;
    static int[][] rooms;
    static int[][] dist;
    static int[][] dp;
    static int INF = (int) 1e9;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());
        H = Integer.parseInt(st.nextToken());
        W = Integer.parseInt(st.nextToken());
        K = Integer.parseInt(st.nextToken());
        st = new StringTokenizer(br.readLine());
        s = Integer.parseInt(st.nextToken());
        t = Integer.parseInt(st.nextToken());
        Tmove = Integer.parseInt(st.nextToken());
        Tcheck = Integer.parseInt(st.nextToken());
        map = new char[H][W];
        for (int i = 0; i < H; i++) {
            map[i] = br.readLine().toCharArray();
        }
        rooms = new int[26][];
        for (int i = 0; i < 26; i++) {
            rooms[i] = new int[0];
        }
        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                if (map[i][j] != '.') {
                    int unit = map[i][j] - 'A';
                    if (rooms[unit].length == 0) {
                        rooms[unit] = new int[]{i, j};
                    } else {
                        rooms[unit][rooms[unit].length - 1] = j;
                    }
                }
            }
        }
        dist = new int[26][26];
        for (int i = 0; i < 26; i++) {
            for (int j = 0; j < 26; j++) {
                dist[i][j] = INF;
            }
        }
        for (int i = 0; i < 26; i++) {
            for (int j = 0; j < rooms[i].length - 1; j++) {
                int x1 = rooms[i][j], y1 = rooms[i][j + 1];
                for (int k = 0; k < 26; k++) {
                    if (k == i) continue;
                    for (int l = 0; l < rooms[k].length - 1; l++) {
                        int x2 = rooms[k][l], y2 = rooms[k][l + 1];
                        dist[i][k] = Math.min(dist[i][k], Math.abs(x1 - x2) + Math.abs(y1 - y2));
                    }
                }
            }
        }
        dp = new int[1 << 26][K + 1];
        for (int i = 0; i < (1 << 26); i++) {
            for (int j = 0; j <= K; j++) {
                dp[i][j] = INF;
            }
        }
        dp[0][0] = 0;
        for (int i = 0; i < (1 << 26); i++) {
            for (int j = 0; j <= K; j++) {
                for (int k = 0; k < 26; k++) {
                    if ((i & (1 << k)) == 0) continue;
                    int newI = i ^ (1 << k);
                    int newJ = j + 1;
                    if (newJ > K) continue;
                    dp[newI][newJ] = Math.min(dp[newI][newJ], dp[i][j] + Tcheck * rooms[k].length + Tmove * dist[k][j]);
                }
            }
        }
        int ans = INF;
        for (int i = 0; i <= K; i++) {
            ans = Math.min(ans, dp[(1 << 26) - 1][i] + Tmove * (H + W - 2) + Tcheck * (rooms[0].length + rooms[1].length + rooms[2].length));
        }
        pw.println(ans);
        pw.close();
    }
}
