import sys
input = sys.stdin.readline

def dfs(x, y, u):
    global ans
    if x == sx and y == sy:
        return
    for i in range(1, K + 1):
        if not vis[i][u]:
            vis[i][u] = 1
            for dx, dy in [(1, 0), (-1, 0), (0, 1), (0, -1)]:
                nx, ny = x + dx, y + dy
                if 0 <= nx < H and 0 <= ny < W and grid[nx][ny] == unit[u]:
                    dfs(nx, ny, u)
            vis[i][u] = 0
            return
    ans += 2 * T_move + T_check * room[u]

H, W, K = map(int, input().split())
sx, sy, T_move, T_check = map(int, input().split())
grid = [list(input().rstrip()) for _ in range(H)]
unit = {}
room = {}
for i in range(H):
    for j in range(W):
        if grid[i][j] != '.':
            u = grid[i][j]
            if u not in unit:
                unit[u] = len(unit) + 1
                room[u] = 0
            room[u] += 1

for i in range(H):
    for j in range(W):
        if grid[i][j] != '.':
            u = grid[i][j]
            for dx, dy in [(1, 0), (-1, 0), (0, 1), (0, -1)]:
                nx, ny = i + dx, j + dy
                if 0 <= nx < H and 0 <= ny < W and grid[nx][ny] == u:
                    grid[i][j] = u
                    break

vis = [[0] * (len(unit) + 1) for _ in range(K + 1)]
ans = 0
for i in range(1, K + 1):
    for u in range(1, len(unit) + 1):
        if not vis[i][u]:
            vis[i][u] = 1
            dfs(0, 0, u)
            vis[i][u] = 0
print(ans)
