#include <stdio.h>

int main() {
    int n, a, b;
    scanf("%d %d %d", &n, &a, &b);

    int sum = 0;
    for (int i = 0; i < n; i++) {
        int x;
        scanf("%d", &x);
        sum += x;
    }

    if (sum % 2 == 0) {
        if (a <= b) {
            printf("Jiro\n");
        } else {
            printf("Hanako\n");
        }
    } else {
        if (a >= b) {
            printf("Hanako\n");
        } else {
            printf("Jiro\n");
        }
    }

    return 0;
}
