#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int n, a, b;
    cin >> n >> a >> b;

    vector<int> s(n);
    for (int i = 0; i < n; i++) {
        cin >> s[i];
    }

    int sum = 0;
    for (int i = 0; i < n; i++) {
        sum += s[i];
    }

    if (sum % 2 == 0 && a <= sum / 2 && b <= sum / 2) {
        cout << "Jiro" << endl;
    } else {
        cout << "Hanako" << endl;
    }

    return 0;
}
