#include <iostream>
#include <vector>
#include <string>
#include <sstream>

int main() {
    int w, h;
    std::cin >> w >> h;

    std::vector<int> a(w);
    std::vector<int> b(h);

    for (int i = 0; i < w; ++i) {
        std::cin >> a[i];
    }

    for (int i = 0; i < h; ++i) {
        std::cin >> b[i];
    }

    int sumA = 0;
    int sumB = 0;

    for (int i = 0; i < w; ++i) {
        sumA += a[i];
    }

    for (int i = 0; i < h; ++i) {
        sumB += b[i];
    }

    if (sumA == w && sumB == h) {
        std::cout << "1";
    } else {
        std::cout << "0";
    }

    return 0;
}
