#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

const int N = 1e5 + 5;

int n;
string s;
vector<int> G[N];
int dep[N], son[N], top[N], dfn[N], cnt;
int st[N], top_son[N], sz[N];
int ans;

void dfs1(int u, int fa) {
    dep[u] = dep[fa] + 1;
    son[u] = 0;
    sz[u] = 1;
    for (int v : G[u]) {
        if (v == fa) continue;
        dfs1(v, u);
        sz[u] += sz[v];
        if (sz[v] > sz[son[u]]) son[u] = v;
    }
}

void dfs2(int u, int topf) {
    top[u] = topf;
    dfn[u] = ++cnt;
    st[++sz[topf]] = u;
    if (son[u]) dfs2(son[u], topf);
    for (int v : G[u]) {
        if (v == son[u] || v == top[u]) continue;
        dfs2(v, v);
    }
    sz[topf] -= 1;
}

int get(int u, int v) {
    return dfn[u] < dfn[v] ? u : v;
}

int calc(int u) {
    if (s[u - 1] == '(') {
        return calc(son[u]) + 1;
    } else {
        int res = 0;
        for (int v : G[u]) {
            if (v == son[u] || v == top[u]) continue;
            res += calc(v);
        }
        return res + 1;
    }
}

int main() {
    cin >> n >> s;
    for (int i = 1; i < n; i++) {
        int u, v;
        cin >> u >> v;
        G[u].push_back(v);
        G[v].push_back(u);
    }
    dfs1(1, 0);
    dfs2(1, 1);
    for (int i = 1; i <= n; i++) {
        int u = i, v = i;
        while (top[u] != top[v]) {
            if (dep[top[u]] > dep[top[v]]) {
                u = top_son[top[u]];
            } else {
                v = top_son[top[v]];
            }
        }
        int len = 0;
        while (u != v) {
            if (dep[u] > dep[v]) {
                len += calc(u);
                u = son[u];
            } else {
                len += calc(v);
                v = son[v];
            }
        }
        len += calc(u);
        ans += len;
    }
    cout << ans << endl;
    return 0;
}
