from collections import deque

def dfs(node, parent, s, adj, depth, depth_map):
    depth_map[node] = depth
    stack = [(node, parent, s, depth)]
    while stack:
        node, parent, s, depth = stack.pop()
        for child in adj[node]:
            if child != parent:
                stack.append((child, node, s + adj[node][child], depth + 1))
                depth_map[child] = depth + 1

def solve():
    n = int(input())
    s = input()
    adj = {i: {} for i in range(1, n + 1)}
    for _ in range(n - 1):
        u, v = map(int, input().split())
        adj[u][v] = adj[v][u] = s[u - 1]
    depth_map = {}
    dfs(1, 0, '', 0, depth_map)
    dp = [[0, 0] for _ in range(n + 1)]
    dp[0][0] = 1
    for i in range(1, n + 1):
        if s[i - 1] == '(':
            dp[i][0] = dp[i - 1][0] + dp[i - 1][1]
        else:
            dp[i][1] = dp[i - 1][0] + dp[i - 1][1]
    ans = 0
    for i in range(1, n + 1):
        for j in range(i + 1, n + 1):
            if depth_map[i] > depth_map[j]:
                continue
            if depth_map[i] == depth_map[j]:
                if s[i - 1] == s[j - 1]:
                    ans += 1
            else:
                if s[i - 1] == ')':
                    ans += dp[depth_map[j] - depth_map[i] - 1][0]
                else:
                    ans += dp[depth_map[j] - depth_map[i] - 1][1]
    print(ans)

solve()
