#include <stdio.h>
#include <stdlib.h>

#define N 605
#define INF 1000000000

int main() {
    int n, k, a, b;
    int x[N], sum[N], min[N], max[N];

    scanf("%d %d %d %d", &n, &k, &a, &b);
    for (int i = 1; i <= n; i++) {
        scanf("%d", &x[i]);
        sum[i] = sum[i - 1] + x[i];
        min[i] = min[i - 1];
        max[i] = max[i - 1];
        if (x[i] < min[i]) min[i] = x[i];
        if (x[i] > max[i]) max[i] = x[i];
    }

    int t = a;
    int u = INF;
    int idx = 0;
    for (int i = a; i <= b; i++) {
        int s = sum[n] - (sum[n] - sum[i - 1]) - (sum[n] - sum[n - k]);
        int m = min[n] - (min[n] - min[i - 1]) - (min[n] - min[n - k]);
        int d = i - m;
        if (d < u) {
            u = d;
            t = i;
            idx = i - 1;
        }
    }

    printf("%d\n", t);
    int s = sum[n] - (sum[n] - sum[t - 1]) - (sum[n] - sum[n - k]);
    int m = min[n] - (min[n] - min[t - 1]) - (min[n] - min[n - k]);
    int d = t - m;
    int p = 0;
    for (int i = 1; i <= n; i++) {
        if (x[i] == d) {
            if (p == 0 || x[i] < x[idx]) {
                idx = i;
                p++;
            }
        }
    }

    for (int i = 1; i <= n; i++) {
        if (i != idx) printf("%d ", i);
    }
    printf("\n");

    return 0;
}
