#include <iostream>
#include <vector>
#include <algorithm>

int main() {
    int n, k, a, b;
    std::cin >> n >> k >> a >> b;

    std::vector<int> x(n);
    for (int i = 0; i < n; ++i) {
        std::cin >> x[i];
    }

    std::vector<int> sorted_x = x;
    std::sort(sorted_x.begin(), sorted_x.end());

    int t = a;
    int min_diff = b - a + 1;
    std::vector<int> chosen;

    for (int i = 0; i < n; ++i) {
        int diff = std::abs(t - sorted_x[i]);
        if (diff < min_diff) {
            min_diff = diff;
            chosen.clear();
            chosen.push_back(i);
        } else if (diff == min_diff) {
            chosen.push_back(i);
        }
    }

    std::sort(chosen.begin(), chosen.end(), [&x](int i, int j) {
        return x[i] < x[j];
    });

    for (int i = 1; i < k; ++i) {
        chosen.push_back(chosen[i - 1] + 1);
        while (chosen.back() >= n || x[chosen.back()] == x[chosen[chosen.size() - 2]]) {
            chosen.pop_back();
        }
    }

    std::cout << t << std::endl;
    for (int i = 0; i < k; ++i) {
        std::cout << chosen[i] + 1 << " ";
    }
    std::cout << std::endl;

    return 0;
}
