import java.util.*;
import java.io.*;

public class Main {
    static class FastReader {
        BufferedReader br;
        StringTokenizer st;

        public FastReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }

        double nextDouble() {
            return Double.parseDouble(next());
        }

        String nextLine() {
            String str = "";
            try {
                str = br.readLine();
            } catch (IOException e) {
                e.printStackTrace();
            }
            return str;
        }
    }

    public static void main(String[] args) {
        FastReader sc = new FastReader();
        PrintWriter out = new PrintWriter(System.out);
        int n = sc.nextInt();
        int k = sc.nextInt();
        int a = sc.nextInt();
        int b = sc.nextInt();
        int[] x = new int[n];
        for (int i = 0; i < n; i++) {
            x[i] = sc.nextInt();
        }
        int[] sum = new int[n + 1];
        for (int i = 0; i < n; i++) {
            sum[i + 1] = sum[i] + x[i];
        }
        int t = a;
        int min = Integer.MAX_VALUE;
        for (int i = a; i <= b; i++) {
            int left = sum[i] - sum[i - k];
            int right = sum[n] - sum[i + k];
            int curr = Math.max(left, right);
            if (curr < min) {
                min = curr;
                t = i;
            }
        }
        out.println(t);
        int[] ans = new int[k];
        int[] idx = new int[n];
        Arrays.fill(idx, -1);
        for (int i = 0; i < n; i++) {
            if (x[i] >= t) {
                idx[i] = 1;
            } else {
                idx[i] = 0;
            }
        }
        int left = sum[t] - sum[t - k];
        int right = sum[n] - sum[t + k];
        int curr = Math.max(left, right);
        for (int i = 0; i < n; i++) {
            if (idx[i] == 1) {
                left -= x[i];
            } else {
                right -= x[i];
            }
            if (left == curr || right == curr) {
                ans[ans.length - 1] = i + 1;
                for (int j = 0; j < ans.length - 1; j++) {
                    out.print(ans[j] + " ");
                }
                out.println(ans[ans.length - 1]);
                out.close();
                return;
            }
        }
    }
}
