import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        int w = scanner.nextInt();
        int h = scanner.nextInt();

        int[] a = new int[w];
        int[] b = new int[h];

        for (int i = 0; i < w; i++) {
            a[i] = scanner.nextInt();
        }

        for (int i = 0; i < h; i++) {
            b[i] = scanner.nextInt();
        }

        boolean isValid = true;

        for (int i = 0; i < w; i++) {
            if (a[i] > h) {
                isValid = false;
                break;
            }
        }

        for (int i = 0; i < h; i++) {
            if (b[i] > w) {
                isValid = false;
                break;
            }
        }

        for (int i = 0; i < w; i++) {
            if (a[i] != 0) {
                int sum = 0;
                for (int j = 0; j < h; j++) {
                    sum += b[j];
                }
                if (sum < a[i]) {
                    isValid = false;
                    break;
                }
            }
        }

        for (int i = 0; i < h; i++) {
            if (b[i] != 0) {
                int sum = 0;
                for (int j = 0; j < w; j++) {
                    sum += a[j];
                }
                if (sum < b[i]) {
                    isValid = false;
                    break;
                }
            }
        }

        System.out.println(isValid ? 1 : 0);
    }
}
