#include <stdio.h>
#include <math.h>

#define N 200

double p[N + 1];
int f[N + 1];

int main() {
    int p_, m, n, i, j, k, l, cnt, max_cnt;
    double max_prob;

    scanf("%d %d %d", &p_, &m, &n);
    p_ /= 100.0;

    for (i = 1; i <= N; i++) {
        p[i] = 0.0;
        f[i] = 0;
    }

    for (i = 1; i <= N; i++) {
        for (j = 1; j <= m; j++) {
            if (i == 1) {
                p[i] += 1.0;
            } else {
                p[i] += p[i - 1] * (1.0 - p_);
            }
            if (j * (1.0 + p_) >= n) {
                f[i] = j;
                break;
            }
        }
    }

    max_prob = 0.0;
    max_cnt = 0;
    for (i = 1; i <= N; i++) {
        if (p[i] > max_prob) {
            max_prob = p[i];
            max_cnt = 1;
        } else if (p[i] == max_prob) {
            max_cnt++;
        }
    }

    printf("%.10lf\n", max_prob);
    printf("%d\n", max_cnt);
    if (max_cnt == 1) {
        for (i = 1; i <= N; i++) {
            if (p[i] == max_prob && f[i] != 0) {
                printf("%d\n", f[i]);
                break;
            }
        }
    } else {
        for (i = 1; i <= N; i++) {
            if (p[i] == max_prob && f[i] != 0) {
                printf("%d ", f[i]);
                break;
            }
