#include <iostream>
#include <vector>
#include <algorithm>
#include <cmath>
#include <iomanip>

using namespace std;

double f(double p, double m, double n, double x) {
    return (1 - pow(1 - p / 100, x)) / (1 - pow(1 - p / 100, m));
}

int main() {
    double p, m, n;
    cin >> p >> m >> n;

    double ans = 0;
    int cnt = 0;
    vector<int> bets;

    for (int i = 1; i <= m; i++) {
        double cur = f(p, m, n, i);
        if (cur > ans) {
            ans = cur;
            bets.clear();
            bets.push_back(i);
            cnt = 1;
        } else if (cur == ans) {
            bets.push_back(i);
            cnt++;
        }
    }

    if (cnt > 200) {
        cout << fixed << setprecision(6) << ans << endl;
        cout << cnt << endl;
        sort(bets.begin(), bets.end());
        cout << bets[0];
        for (int i = 1; i < 100; i++) {
            cout << " " << bets[i];
        }
        cout << " ";
        for (int i = bets.size() - 101; i < bets.size(); i++) {
            cout << bets[i];
        }
        cout << endl;
    } else {
        cout << fixed << setprecision(6) << ans << endl;
        cout << cnt << endl;
        for (int i = 0; i < bets.size(); i++) {
            cout << bets[i];
            if (i != bets.size() - 1) {
                cout << " ";
            }
        }
        cout << endl;
    }

    return 0;
}
