#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

#define N 2000
#define M 1000000000
#define INF 1000000000

typedef struct {
    int to;
    int cost;
    int next;
} Edge;

int n, m;
int s[N + 1];
int d[N + 1];
int f[N + 1][N + 1];
int e[N + 1][N + 1];
int head[N + 1];
int cnt;

void add_edge(int u, int v, int c) {
    e[u][head[u]++] = v;
    e[v][head[v]++] = u;
}

void dfs(int u, int fa) {
    for (int i = head[u]; i--; ) {
        int v = e[u][i];
        if (v == fa) continue;
        dfs(v, u);
        d[u] = d[v] + 1;
    }
}

void dfs2(int u, int fa, int d) {
    f[u][d] = s[u];
    for (int i = head[u]; i--; ) {
        int v = e[u][i];
        if (v == fa) continue;
        dfs2(v, u, d + 1);
        f[u][d] = (f[u][d] > f[v][d]) ? f[u][d] : f[v][d];
    }
}

int main() {
    scanf("%d %d", &n, &m);
    for (int i = 1; i < n; i++) {
        int u, v, c;
        scanf("%d %d %d", &u, &v, &c);
        add_edge(u, v, c);
    }
    for (int i = 1; i <= n; i++) {
        scanf("%d", &s[i]);
    }
    dfs(1, 0);
    dfs2(1, 0, 0);
    int ans = 0;
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            ans = (ans > f[i][j] * d[i]) ? ans : f[i][j] * d[i];
        }
    }
    printf("%d\n", ans * m);
    return 0;
}
