#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

const int N = 2005;

struct Edge {
    int to, w;
};

vector<Edge> g[N];
int n, m, s[N], f[N][N], d[N], p[N];
long long ans;

void dfs(int u, int fa) {
    for (auto e : g[u]) {
        int v = e.to;
        if (v == fa) continue;
        d[v] = d[u] + e.w;
        p[v] = u;
        dfs(v, u);
    }
}

int main() {
    cin >> n >> m;
    for (int i = 1; i < n; i++) {
        int u, v, w;
        cin >> u >> v >> w;
        g[u].push_back({v, w});
        g[v].push_back({u, w});
    }
    for (int i = 1; i <= n; i++) cin >> s[i];
    dfs(1, 0);
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            f[i][j] = 1e9;
        }
    }
    for (int i = 1; i <= n; i++) {
        f[i][i] = 0;
    }
    for (int k = 1; k <= n; k++) {
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                f[i][j] = min(f[i][j], f[i][k] + f[k][j]);
            }
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            ans = max(ans, (long long)s[i] * f[i][j] * min(d[i], d[j]));
        }
    }
    cout << ans * m << endl;
    return 0;
}
