#include <iostream>
#include <vector>
#include <queue>
#include <limits>

using namespace std;

typedef long long ll;
typedef pair<int, int> pii;

const int MAXN = 105;
const int INF = numeric_limits<int>::max();

int N, M, s, t;
vector<pii> adj[MAXN];
int cap[MAXN][MAXN], cost[MAXN][MAXN];
int dist[MAXN], parent[MAXN];

void addEdge(int u, int v, int c, int w) {
    adj[u].push_back({v, c});
    adj[v].push_back({u, 0});
    cap[u][v] = c;
    cost[u][v] = w;
}

int minCostFlow() {
    int flow = 0, cost = 0;
    while (true) {
        fill(dist, dist + N, INF);
        fill(parent, parent + N, -1);
        dist[s] = 0;
        queue<int> q;
        q.push(s);
        while (!q.empty()) {
            int u = q.front();
            q.pop();
            for (auto [v, c] : adj[u]) {
                if (c > 0 && dist[v] > dist[u] + cost[u][v]) {
                    dist[v] = dist[u] + cost[u][v];
                    parent[v] = u;
                    q.push(v);
                }
            }
        }
        if (dist[t] == INF) break;
        int u = t;
        int f = INF;
        while (u != s) {
            int p = parent[u];
            f = min(f, cap[p][u]);
            u = p;
        }
        u = t;
        while (u != s) {
            int p = parent[u];
            cap[p][u] -= f;
            cap[u][p] += f;
            u = p;
        }
        flow += f;
        u = t;
        while (u != s) {
            int p = parent[u];
            cost[u] += cost[p][u] * f;
            u = p;
        }
        cost += dist[t];
    }
    return flow;
}

int main() {
    cin >> N >> M;
    cin >> s >> t;
    s--; t--;
    for (int i = 0; i < M; i++) {
        int u, v, c, w;
        cin >> u >> v >> c >> w;
        u--; v--;
        addEdge(u, v, c, w);
    }
    int flow = minCostFlow();
    int maxFlow = flow;
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < N; j++) {
            if (i != j && cap[i][j] > 0) {
                cap[i][j] = INF;
                cap[j][i] = 0;
                int f = minCostFlow();
                if (f > maxFlow) {
                    maxFlow = f;
                }
                cap[i][j] = 0;
                cap[j][i] = INF;
            }
        }
    }
    int minCost = 0;
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < N; j++) {
            if (i != j && cap[i][j] > 0) {
                minCost += cap[i][j] * cost[i][j];
            }
        }
    }
    int ans = (maxFlow - minCost) * (maxFlow + minCost);
    cout << ans << "/" << 1 << endl;
    return 0;
}
