#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 1000000009
#define N 3000000

typedef struct {
    int u, v, c;
} Edge;

int n, m, cnt, deg[N], col[N], vis[N];
Edge e[N];
int g[N][N], f[N][1 << 18];

void add(int u, int v) {
    g[u][v] = g[v][u] = 1;
}

void dfs(int u) {
    vis[u] = 1;
    for (int v = 1; v <= 3 * n; v++) {
        if (g[u][v] && !vis[v]) {
            dfs(v);
        }
    }
}

int main() {
    scanf("%d%d", &n, &m);
    for (int i = 1; i <= m; i++) {
        scanf("%d%d%d", &e[i].u, &e[i].v, &e[i].c);
        add(e[i].u, e[i].v);
        deg[e[i].u]++, deg[e[i].v]++;
    }
    for (int i = 1; i <= 3 * n; i++) {
        if (!deg[i]) {
            dfs(i);
        }
    }
    for (int i = 1; i <= m; i++) {
        if (col[e[i].u] == col[e[i].v]) {
            if (e[i].c == 0) {
                printf("0\n");
                return 0;
            }
        } else {
            if (e[i].c == 1) {
                printf("0\n");
                return 0;
            }
        }
        col[e[i].u] = col[e[i].v] = i;
    }
    for (int i = 1; i <= 3 * n; i++) {
        f[i][0] = 1;
    }
    for (int i = 1; i <= m; i++) {
        for (int j = 1; j <= 3 * n; j++) {
            f[j][1 << (i - 1)] = 0;
        }
        for (int j = 1; j <= 3 * n; j++) {
            for (int k = 0; k < (1 << m); k++) {
                if (k & (1 << (i - 1))) {
                    f[j][k ^ (1 << (i - 1))] = (f[j][k ^ (1 << (i - 1))] + f[j][k]) % MOD;
                }
                if (g[j][e[i].u] && (k & (1 << (i - 1)))) {
                    f[e[i].u][k ^ (1 << (i - 1))] = (f[e[i].u][k ^ (1 << (i - 1))] + f[j][k] * (k & (1 << (i - 1))) % MOD) % MOD;
                }
                if (g[j][e[i].v] && (k & (1 << (i - 1)))) {
                    f[e[i].v][k ^ (1 << (i - 1))] = (f[e[i].v][k ^ (1 << (i - 1))] + f[j][k] * (k & (1 << (i - 1))) % MOD) % MOD;
                }
            }
        }
    }
    long long ans = 0;
    for (int i = 1; i <= 3 * n; i++) {
        for (int j = 0; j < (1 << m); j++) {
            if (j & (1 << (m - 1))) {
                ans = (ans + f[i][j]) % MOD;
            }
        }
    }
    printf("%lld\n", ans);
    return 0;
}
