#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const int MOD = 1e9 + 9;

int add(int a, int b) {
    return (a + b) % MOD;
}

int mul(int a, int b) {
    return (int)((long long)a * b % MOD);
}

int binpow(int a, int b) {
    int res = 1;
    while (b) {
        if (b & 1) res = mul(res, a);
        a = mul(a, a);
        b >>= 1;
    }
    return res;
}

int inv(int a) {
    return binpow(a, MOD - 2);
}

int main() {
    int n, m;
    cin >> n >> m;

    vector<int> a(m), b(m), c(m);
    for (int i = 0; i < m; ++i) {
        cin >> a[i] >> b[i] >> c[i];
        --a[i]; --b[i];
    }

    vector<vector<int>> g(3 * n);
    for (int i = 0; i < m; ++i) {
        g[a[i]].push_back(b[i]);
        g[b[i]].push_back(a[i]);
    }

    vector<int> col(3 * n);
    vector<int> used(3 * n);
    int res = 1;

    function<void(int)> dfs = [&](int v) {
        used[v] = 1;
        for (int u : g[v]) {
            if (col[u] == -1) {
                col[u] = col[v] ^ c[lower_bound(g[u].begin(), g[u].end(), v) - g[u].begin()];
                dfs(u);
            } else if (col[u] != (col[v] ^ c[lower_bound(g[u].begin(), g[u].end(), v) - g[u].begin()])) {
                res = 0;
            }
        }
    };

    for (int i = 0; i < 3 * n; ++i) {
        if (!used[i]) {
            col[i] = -1;
            dfs(i);
        }
    }

    if (res) {
        vector<int> dp(1 << m);
        dp[0] = 1;
        for (int i = 0; i < (1 << m); ++i) {
            for (int j = 0; j < m; ++j) {
                if (!(i & (1 << j))) {
                    int mask = i | (1 << j);
                    int cnt = __builtin_popcount(mask);
                    if (cnt % 3 == 0) {
                        dp[mask] = add(dp[mask], mul(dp[i], 3));
                    } else if (cnt % 3 == 1) {
                        dp[mask] = add(dp[mask], mul(dp[i], 2));
                    } else {
                        dp[mask] = add(dp[mask], dp[i]);
                    }
                }
            }
        }
        cout << dp[(1 << m) - 1] << '\n';
    } else {
        cout << 0 << '\n';
    }

    return 0;
}
