from collections import defaultdict
import sys
input = sys.stdin.readline

MOD = 10**9 + 9

def add(x, y):
    return (x + y) % MOD

def mul(x, y):
    return (x * y) % MOD

def pow(x, y):
    res = 1
    while y:
        if y & 1:
            res = mul(res, x)
        x = mul(x, x)
        y >>= 1
    return res

def solve():
    N, M = map(int, input().split())
    N *= 3
    G = defaultdict(list)
    for _ in range(M):
        u, v, c = map(int, input().split())
        u -= 1
        v -= 1
        G[u].append((v, c))
        G[v].append((u, c))

    dp = [0] * (1 << M)
    dp[0] = 1
    for mask in range(1 << M):
        for i in range(M):
            if (mask >> i) & 1:
                for j in range(M):
                    if (mask >> j) & 1 and i != j:
                        u, v = i, j
                        while u != v:
                            if G[u][0][0] > G[v][0][0]:
                                u, v = v, u
                            if G[u][0][0] == G[v][0][0]:
                                if G[u][0][1] > G[v][0][1]:
                                    u, v = v, u
                            if G[u][0][0] < G[v][0][0]:
                                break
                            if G[u][0][0] == G[v][0][0]:
                                if G[u][0][1] < G[v][0][1]:
                                    break
                            if G[u][0][0] > G[v][0][0]:
                                break
                            u = G[u][0][0]
                            v = G[v][0][0]
                        if G[u][0][1] == 0:
                            dp[mask ^ (1 << i) ^ (1 << j)] = add(dp[mask ^ (1 << i) ^ (1 << j)], mul(dp[mask], pow(2, N - 3 * __builtin_popcount(mask))))
                        else:
                            dp[mask ^ (1 << i) ^ (1 << j)] = add(dp[mask ^ (1 << i) ^ (1 << j)], mul(dp[mask], pow(2, N - 3 * __builtin_popcount(mask) - 1)))
    print(dp[-1])

solve()
