#include <stdio.h>
#include <stdlib.h>

#define N 55

int dx[N] = {1, 0, -1, 0};
int dy[N] = {0, 1, 0, -1};

int main() {
    int n, x, y;
    scanf("%d %d %d", &n, &x, &y);

    int d[N], ll[N], lu[N];
    for (int i = 0; i < n; i++) {
        char c;
        scanf(" %c %d %d", &c, &ll[i], &lu[i]);
        if (c == '?') d[i] = -1;
        else d[i] = c == 'L' ? 0 : 1;
    }

    int dir = 0;
    int px = 0, py = 0;
    int ok = 1;
    for (int i = 0; i < n; i++) {
        int l, u;
        if (d[i] == -1) {
            for (l = 1; l <= lu[i]; l++) {
                int nx = px + dx[dir] * l;
                int ny = py + dy[dir] * l;
                if (nx == x && ny == y) {
                    d[i] = dir;
                    break;
                }
            }
            if (l > lu[i]) ok = 0;
        }
        if (d[i] == -1) continue;
        int nx = px + dx[dir] * (lu[i] - ll[i] + 1);
        int ny = py + dy[dir] * (lu[i] - ll[i] + 1);
        if (nx != x || ny != y) ok = 0;
        px = x - dx[dir] * (lu[i] - ll[i] + 1);
        py = y - dy[dir] * (lu[i] - ll[i] + 1);
        printf("%c %d\n", "LR"[d[i]], lu[i] - ll[i] + 1);
        dir = (dir + d[i] + 2) % 4;
    }

    if (ok) printf("%d\n", n);
    else printf("-1\n");

    return 0;
}
