#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

int main() {
    int n, x, y;
    cin >> n >> x >> y;

    vector<int> dx = {1, 0, -1, 0};
    vector<int> dy = {0, 1, 0, -1};

    vector<int> dir(n);
    vector<int> ll(n), lu(n);

    for (int i = 0; i < n; i++) {
        char c;
        cin >> c;
        if (c == 'L') dir[i] = 0;
        else if (c == 'R') dir[i] = 1;
        else dir[i] = 2;

        cin >> ll[i] >> lu[i];
    }

    int curx = 0, cury = 0;
    int curdir = 0;

    vector<int> ans(n);

    for (int i = 0; i < n; i++) {
        if (dir[i] == 2) {
            int l = ll[i], r = lu[i];
            if (l == r) {
                ans[i] = l;
                curx += dx[curdir] * l;
                cury += dy[curdir] * l;
            } else {
                for (int j = l; j <= r; j++) {
                    if (curx + dx[curdir] * j >= x && cury + dy[curdir] * j >= y) {
                        ans[i] = j;
                        curx += dx[curdir] * j;
                        cury += dy[curdir] * j;
                        break;
                    }
                }
                if (curx != x || cury != y) {
                    cout << -1 << endl;
                    return 0;
                }
            }
        } else {
            ans[i] = ll[i];
            curx += dx[curdir] * ans[i];
            cury += dy[curdir] * ans[i];
            curdir = (curdir + dir[i] + 2) % 4;
        }
    }

    if (curx != x || cury != y) {
        cout << -1 << endl;
        return 0;
    }

    cout << n << endl;
    for (int i = 0; i < n; i++) {
        if (dir[i] == 2) {
            cout << (i == 0 ? 'L' : 'R') << " " << ans[i] << endl;
        } else {
            cout << (dir[i] == 0 ? "L" : "R") << " " << ans[i] << endl;
        }
    }

    return 0;
}
