import java.util.*;
import java.io.*;

public class Main {
    static int N, X, Y;
    static int[][] D = new int[55][3];
    static int[] LL = new int[55];
    static int[] LU = new int[55];
    static int[] dir = new int[]{1, 0, -1, 0};
    static int[] pos = new int[]{0, 0};
    static int[] cur = new int[]{1, 0};
    static int[] ans = new int[55];
    static int[] len = new int[55];

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        N = Integer.parseInt(st.nextToken());
        X = Integer.parseInt(st.nextToken());
        Y = Integer.parseInt(st.nextToken());
        for (int i = 1; i <= N; i++) {
            st = new StringTokenizer(br.readLine());
            D[i][0] = st.nextToken().charAt(0) - 'L';
            D[i][1] = Integer.parseInt(st.nextToken());
            D[i][2] = Integer.parseInt(st.nextToken());
        }
        if (solve(1)) {
            System.out.println(N);
            for (int i = 1; i <= N; i++) {
                System.out.println((char) (D[i][0] + 'L') + " " + len[i]);
            }
        } else {
            System.out.println(-1);
        }
    }

    static boolean solve(int i) {
        if (i > N) {
            if (pos[0] == X && pos[1] == Y) {
                return true;
            } else {
                return false;
            }
        }
        if (D[i][0] == -1) {
            for (int j = 1; j <= D[i][2] - D[i][1] + 1; j++) {
                len[i] = D[i][1] + j - 1;
                if (solve(i + 1)) {
                    return true;
                }
                len[i] = D[i][1] + j;
            }
            return false;
        } else {
            len[i] = D[i][1];
            if (solve(i + 1)) {
                return true;
            }
            len[i] = D[i][2];
            if (solve(i + 1)) {
                return true;
            }
            return false;
        }
    }
}
