#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MAX 100000

typedef struct {
    int x, y;
} Point;

int compare_x(const void *a, const void *b) {
    Point *p1 = (Point *)a;
    Point *p2 = (Point *)b;
    return p1->x - p2->x;
}

int compare_y(const void *a, const void *b) {
    Point *p1 = (Point *)a;
    Point *p2 = (Point *)b;
    return p1->y - p2->y;
}

int main() {
    int L, M, N;
    scanf("%d %d %d", &L, &M, &N);

    Point A[MAX], B[MAX], C[MAX];

    for (int i = 0; i < L; i++) {
        scanf("%d %d", &A[i].x, &A[i].y);
    }

    for (int i = 0; i < M; i++) {
        scanf("%d %d", &B[i].x, &B[i].y);
    }

    for (int i = 0; i < N; i++) {
        scanf("%d %d", &C[i].x, &C[i].y);
    }

    qsort(A, L, sizeof(Point), compare_x);
    qsort(B, M, sizeof(Point), compare_x);
    qsort(C, N, sizeof(Point), compare_x);

    int count = 0;

    for (int i = 0; i < L; i++) {
        for (int j = 0; j < M; j++) {
            int k = 0;
            while (k < N) {
                if (C[k].x < (A[i].x + B[j].x) / 2) {
                    k++;
                } else if (C[k].x > (A[i].x + B[j].x) / 2) {
                    break;
                } else {
                    if (C[k].y == (A[i].y + B[j].y) / 2) {
                        count++;
                    }
                    k++;
                }
            }
        }
    }

    printf("%d\n", count);

    return 0;
}
