#include <iostream>
#include <vector>
#include <map>
#include <algorithm>

using namespace std;

struct Point {
    int x, y;
};

bool cmp(const Point& a, const Point& b) {
    return a.x < b.x || (a.x == b.x && a.y < b.y);
}

int main() {
    int L, M, N;
    cin >> L >> M >> N;

    vector<Point> A(L), B(M), C(N);
    for (int i = 0; i < L; i++) {
        cin >> A[i].x >> A[i].y;
    }
    for (int i = 0; i < M; i++) {
        cin >> B[i].x >> B[i].y;
    }
    for (int i = 0; i < N; i++) {
        cin >> C[i].x >> C[i].y;
    }

    sort(A.begin(), A.end(), cmp);
    sort(B.begin(), B.end(), cmp);
    sort(C.begin(), C.end(), cmp);

    map<pair<int, int>, int> A_map, B_map;
    for (int i = 0; i < L; i++) {
        A_map[{A[i].x, A[i].y}] = i;
    }
    for (int i = 0; i < M; i++) {
        B_map[{B[i].x, B[i].y}] = i;
    }

    int ans = 0;
    for (int i = 0; i < N; i++) {
        int x = C[i].x, y = C[i].y;
        int a = A_map[{x, y}];
        int b = B_map[{x, y}];
        if (a != -1 && b != -1) {
            int l = 0, r = L - 1;
            while (l <= r) {
                int mid = (l + r) / 2;
                if (A[mid].x <= x && A[mid].y <= y) {
                    l = mid + 1;
                } else {
                    r = mid - 1;
                }
            }
            int al = l - 1;
            l = 0, r = M - 1;
            while (l <= r) {
                int mid = (l + r) / 2;
                if (B[mid].x <= x && B[mid].y <= y) {
                    l = mid + 1;
                } else {
                    r = mid - 1;
                }
            }
            int bl = l - 1;
            ans += (al - a + 1) * (bl - b + 1);
        }
    }

    cout << ans << endl;

    return 0;
}
